import React, { Component } from "react";
import Button from "../../components/UI/Button/Button";
import Styles from "./Auth.module.css";


class Auth extends Component {
    state = {
        existingUser:false
    }

    existingUserHandler = () => {
        this.setState({existingUser:!this.state.existingUser})
    }
    render(){

        let form = null

        if(!this.state.existingUser) {
            form = 
            <>
                <div className={Styles.Name}>Sign Up</div>
                <form className={Styles.Form}>
                    <input type="text" placeholder="E-mail"/>
                    <input type="password" placeholder="Password"/>
                    <input type="password" placeholder="Repeat Password"/>
                </form>
                <div className={Styles.ButtonsDiv}>
                    <Button classname="Success" >Submit</Button>
                    <Button classname="Neutral" onclick={this.existingUserHandler}>Login</Button>
                </div>   
            </>
        } else {
            form =
            <>
                <div className={Styles.Name}>Login</div>
                <form className={Styles.Form}>
                    <input type="text" placeholder="E-mail"/>
                    <input type="password" placeholder="Password"/>
                </form>
                <div className={Styles.ButtonsDiv}>
                    <Button classname="Success" >Submit</Button>
                    <Button classname="Neutral" onclick={this.existingUserHandler}>Sign Up</Button>
                </div>   
            </>
        }
        return(
            <>{form}</>
        )
    }
}

export default Auth;
