import React from "react";
import AddProductNutritionLabel from "../../NutritionLabel/AddProductNutritionLabel/AddProductNutritionLabel";
import Styles from "./AddProductModal.module.css";

const addProductModal = (props) => {

    let addedProduct = {
        name:"",
        quantity:100,
        units:"g",
        totalNutrients : {
            ENERC_KCAL:{
              label : "Energy",
              quantity : 0,
              unit : "kcal"
            },
            FAT : {
              label : "Fat",
              quantity : 0,
              unit : "g"
            },
            FASAT : {
              label : "Saturated",
              quantity : 0,
              unit : "g"
            },
            FATRN : {
              label : "Trans",
              quantity : 0,
              unit : "g"
            },
            CHOCDF : {
              label : "Carbs",
              quantity : 0,
              unit : "g"
            },
            FIBTG : {
              label : "Fiber",
              quantity : 0,
              unit : "g"
            },
            SUGAR : {
              label : "Sugars",
              quantity : 0,
              unit : "g"
            },
            PROCNT : {
              label : "Protein",
              quantity : 0,
              unit : "g"
            },
            CHOLE : {
              label : "Cholesterol",
              quantity : 0,
              unit : "mg"
            },
            NA : {
              label : "Sodium",
              quantity : 0,
              unit : "mg"
            },
            CA:{
              label:"Calcium",
              quantity: 0,
              unit:"mg"
            },
            FE:{
              label:"Iron",
              quantity:0,
              unit:"mg"
            },
            VITA_RAE:{
              label:"Vitamin A",
              quantity:0,
              unit:"µg"
            },
            VITC:{
              label:"Vitamin C",
              quantity:0,
              unit:"mg"
          }
        }
    }

    const addName = (event) => {
        addedProduct.name = event.target.value
    }

    const addNutrients = (event) => {
        addedProduct.totalNutrients[event.target.name] = event.target.value
    }
    return(
        <form>
            <i className={`fas fa-times ${Styles.CloseIcon}`} onClick={props.closeIconClicked}></i>
            <div className={Styles.Name}>Add New Product</div>
            <AddProductNutritionLabel addName={addName} addNutrients={addNutrients}/>
            <div>
                <button onClick={props.buttonClickced}>Add Product</button>
            </div>

        </form> 
)}

export default addProductModal;
