import * as actionTypes from "../actions/actionTypes";

const initialState = {
    showAuthModal: true
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.CLOSE_SIGN_UP:
            return {
                ...state,
                showAuthModal:false
            }
        case actionTypes.OPEN_SIGN_UP:
            return{
                ...state,
                showAuthModal:true
            }
        default : return state
    }
}

export default reducer;
