import React from "react";
import Aux from "../../../hoc/Aux/Aux";
import NutritionLabel from "../../NutritionLabel/NutritionLabel";
import Styles from "./ProductModal.module.css";

const productModal = (props) => (
    <Aux>
        <div className={Styles.Name}>{props.name}</div>
        <NutritionLabel/>
        <button onClick={props.buttonClicked}>Select Product</button>
        <input type="text" placeholder="0" className={Styles.Name} value={props.inputValue} onChange={props.inputChanged}/> g
    </Aux> 
)

export default productModal;
