import React from "react";
import { connect } from "react-redux";
import Modal from "../../components/UI/Modal/Modal";
import RecipieModal from "../../components/Recipie/RecipieModal/RecipieModal"
import ItemsList from "../../components/ItemsList/ItemsList";
import NutritionLabel from "../../components/NutritionLabel/NutritionLabel";
import * as actions from "../../Store/actions/productsList";

const home = (props) =>{

    // const addName = (event) => 
    //     { console.log(props.recipieAsProduct.name )
    //         return(
    //         [...props.recipieAsProduct],
    //         props.recipieAsProduct.name = event.target.value
    //         )}


    return(
            <>
                <Modal show={props.showModal}>
                    <RecipieModal
                        recipie={props.recipieAsProduct}
                        closeIconClicked={props.onCloseModal}
                        addName={event => props.onAddRecipieName(event.EventTarget.value)}/>
                </Modal>
                <ItemsList 
                    addProductClicked={() =>props.history.push("/productlist")} 
                    selectedProducts={props.selectedProducts}
                    saveRecipieClicked={() => props.onSaveRecipieClicked(props.recipieAsProduct)}/>
                <NutritionLabel product={props.recipieAsProduct}/>
            </>
)}
const mapStateToProps = state => {
    return {
        recipieAsProduct : state.reciepieNutrients,
        selectedProducts : state.selectedProducts,
        showModal : state.showModal
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onSaveRecipieClicked :(product) => dispatch(actions.productClicked(product)),
        onCloseModal:() => dispatch(actions.closeModal()),
        onAddRecipieNane: (recipieName) => dispatch(actions.addRecipieName(recipieName)) 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(home);
