import * as actionTypes from "../actions/actionTypes";

const initialState = {
    selectedProducts:[],
    clickedProduct: "",
    showModal:false,
    reciepieNutrition : {   
            name : "",
            quantity : 100,
            units : "g",
            totalNutrients : {
                ENERC_KCAL : {quantity : 0},
                FAT : {quantity : 0},
                FASAT : {quantity : 0},
                FATRN : {quantity : 0},
                CHOCDF : {quantity : 0},
                FIBTG : {quantity : 0},
                SUGAR : {quantity : 0},
                PROCNT : {quantity : 0},
                CHOLE : {quantity : 0},
                NA : {quantity : 0},
                CA : {quantity: 0},
                FE : {quantity: 0},
                VITA_RAE : {quantity : 0},
                VITC : {quantity : 0}
            }
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.SELECT_PRODUCT: 
            action.product.quantity = action.amount
            return {
                ...state,
                selectedProducts: state.selectedProducts.concat(action.product),
                showModal:false,
            }
        case actionTypes.CLICKED_PRODUCT:
            return {
                ...state,
                clickedProduct: action.product,
                showModal:true
            }
        case actionTypes.CLOSE_MODAL:
            return {
                ...state,
                showModal:false
            }
        default: return state
    }
}

export default reducer;
