import React, { Component } from "react";
import Styles from "./ItemsList.module.css";

class ItemsList extends Component {
    state = {
        itemList: {
            cheese:{
                nutrition:null,
                name: "Cheese",
                amount:{    
                    amount:100,
                    units:"g"
                }
            },
            salt:{
                nutrition:null,
                name: "Salt",
                amount:{   
                    amount:1,
                    units:"spoons"
                }
            },
            egg:{
                nutrition:null,
                name: "Egg",
                amount: {
                    amount:1,
                    units:null
                }
            }
        }
    } 

    render(){
        return(
            <div className={Styles.ItemsList}>
                <span>Plus </span><input type="text" placeholder="search bar"/>
                <hr/>
                {this.state.itemList.map(item => (
                    <p><span>plus </span>{item.name}<input type="text" placeholder={item.amount.amount}/>{item.amount.units}</p>
                ))}
            </div>
        )
    }
}

export default ItemsList;
