import React from "react";
import { Doughnut } from "react-chartjs-2";
import Styles from "./Product.module.css";

const gramsToPercentsHandler = (props, grams) => (
    props.fatCalories+props.carbohydratesCalories+props.proteinCalories
)

const product = (props) =>(
    <div className={Styles.Product}>
        <div><h1>{props.name}</h1></div>
        <div className={Styles.Doughnut}>
            <Doughnut 
            data={{
                labels:["Fat", "Carbohydrates", "Protein"],
                datasets: [{
                    data:[props.fatCalories,props.carbohydratesCalories, props.proteinCalories],
                    backgroundColor:["#FF6384","#FFCE56","#36A2EB"]
                },]
            }}
            options={{
                legend:{
                    display: false,
                    position:"left"
                },
                tooltips: {
                    callbacks: {
                        value:(item,data)=>{}
                    }
                }
            }}/>
        </div>
    </div>
)

export default product
