import * as actionTypes from "../actions/actionTypes";

const initialState = {
    selectedProducts:null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.SELECT_INGREDIENT: return state
        default: return state
    }
}

export default reducer;
